import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { ruLang as lang, table_localization as localization } from "../../_constants";

import { usersActions } from '../../_actions';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(usersActions.logout());

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        const { dispatch } = this.props;
        if (username && password) {
            dispatch(usersActions.login(username, password));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
        <div>
            <div>
            <center>
            <p>{lang.AUTHORIZATION}</p>
            </center>
            </div>
            <div>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">{lang.EMAIL}</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">{lang.ERR01}</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">{lang.PASSWORD}</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">{lang.ERR02}</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">{lang.MENU_LOGIN}</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    <Link to="/registration">{lang.REGISTRATION}</Link> |
                    <Link to="/recovery"> {lang.RECOVERY_PASSWORD}</Link>
                </form>
            </div>
        </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as LoginPage };
